INSERT INTO `tb_movie_detail` VALUES ('114', '6874166', '马丁·寇霍文', '达科塔·范宁盖·皮尔斯基特·哈灵顿卡里斯·范·侯登保罗·安德森艾米莉亚·琼斯艾薇·乔治薇拉·维塔利威廉·休斯顿卡拉·朱里杰克·罗思娜奥米·巴特里克艾德里安·斯帕克斯多里斯·洛夫杰克·霍林顿', '2016', '0', '0', null);
INSERT INTO `tb_movie_detail` VALUES ('42', '49', '菲丽希缇·琼斯 / 迭戈·鲁纳 / 甄子丹 / 本·门德尔森 / 麦斯·米科尔森 / 艾伦·图代克 / 福里斯特·惠特克 / 姜文 / 里兹·阿迈德 / 乔纳森·阿里斯 / 尤妮斯·奥卢米德 / 克莱尔·陈 / 吉米·斯密茨 / 沃维克·戴维斯 / 马克·普雷斯顿 / 吉娜薇·欧瑞丽 / 詹姆斯·厄尔·琼斯 ', '加里斯·爱德华斯', '&lt;span style=&quot;color:#666666;font-family:&amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;这是一个战火频燃、纷争不断的动荡时代，一群有志之士集结在一起，计划盗走帝国大规模杀伤性武器“死星”的设计图。这个在《星球大战》系列里非常著名的重点事件 ，让一群平凡普通人结成了同盟，决定为世界的改变做出贡献；而在绝密行动的进行中，他们也逐渐成长为顶天立地的英雄。&lt;/span&gt;', '10', '6730000', '0000-00-00 00:00:00');
INSERT INTO `tb_movie_detail` VALUES ('43', '50', '米拉·乔沃维奇 / 伊恩·格雷 / 艾丽·拉特 / 鲁比·罗丝 / 李准基 / 肖恩·罗伯茨 / 威廉·莱维 / 欧文·马肯 / 罗拉 / 艾尔·安德森 / 密尔顿·施尔 / 西沃恩·霍奇森 / 凯文·奥托 / 保罗·汉普赛尔', '保罗·安德森', '&lt;span style=&quot;color:#666666;font-family:&amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;爱丽丝（米拉·乔沃维奇 Milla Jovovich 饰）在华盛顿特区被威斯克背叛后身陷险境，人类几乎要失去最后的希望。作为唯一的幸存者，也是人类对抗僵尸大军的最后防线，爱丽丝必须回到噩梦开始的地方——浣熊市，才能完成拯救世界救赎人类的正义使命。回归故事发生的起点浣熊市，爱丽丝将和昔日的朋友一起', '18', '87340000', '0000-00-00 00:00:00');
INSERT INTO `tb_movie_detail` VALUES ('44', '51', '迈克尔·法斯宾德 / 玛丽昂·歌迪亚 / 杰瑞米·艾恩斯 / 布莱丹·格里森 / 夏洛特·兰普林 / 迈克尔·威廉姆斯 / 丹尼斯·门诺切特 / 亚里安妮·拉贝德 / 赫立德·阿卜杜拉 / 艾斯·戴维斯 / 马蒂亚斯·瓦雷拉 / 卡勒姆·特纳 / 卡洛斯·巴登 / 哈维尔·古铁雷斯 / 霍威克·库区科利安', '贾斯汀·库泽尔', '&lt;span style=&quot;color:#666666;font-family:&amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;卡勒姆·林奇（迈克尔·法斯宾德 饰）在死刑即将执行之前清醒过来，发现他被索菲娅（玛丽昂·歌迪亚 饰）选中，来参加一个能让人类摆脱暴力冲动的计划。虚拟现实机器Animus能让用户体验祖先的记忆，被绑在机器上之后，卡勒姆·林奇意识到他是生活在西班牙宗教法庭时期一位刺客阿圭拉的后裔，他们寻找的是可以控', '23', '5623000', '0000-00-00 00:00:00');

-- ----------------------------
-- Table structure for tb_music
-- ----------------------------
DROP TABLE IF EXISTS `tb_music`;
CREATE TABLE `tb_music` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `music_name` varchar(255) NOT NULL,
  `singer` varchar(255) NOT NULL,
  `movie_name` varchar(255) NOT NULL,
  `pic` varchar(255) DEFAULT NULL,
  `url` varchar(255) DEFAULT NULL,
  `content` mediumtext,
  `time_long` time NOT NULL,
  `listorder` int(8) NOT NULL DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=53 DEFAULT CHARSET=utf8;

-- ----------------------------
